import Cardlist from "./Carlist";
import Searchbox from "./Searchbox";
import { robots } from "./robots";

class App extends Component {
    constructor() {
        super()
        this.state = {
            robots: robots,
            Searchfield: ''
        }
    }

    onSearchChange (event) {
        console.log(event.targert.value);
    }
    render() {
        return  (
            <div className='tc'>
                <h1>RoboFriends</h1>    
                < Searchbox serarchChange ={this.onSearchChange}/>
                <Cardlist robots={this.state.robots}/>
            </div>
        );
    }    
}

export default App;
